mapper.update:
		      UPDATE member SET lv_idx = #{lv_idx}

    				, admin_yn = TRUE

 			   WHERE id = #{id}

mapper.select:
SELECT m.id, m.admin_yn, m.block_yn, m.user_del_yn, m.join_date, m.access_date, bl.block_start_date, bl.block_end_date FROM member m LEFT JOIN ( SELECT blocked_id, block_start_date, block_end_date FROM block_list WHERE (blocked_id, block_start_date) IN ( SELECT blocked_id, MAX(block_start_date) FROM block_list GROUP BY blocked_id ) ) bl ON m.id = bl.blocked_id
AND m.id LIKE CONCAT('%', #{searchId}, '%')
AND m.admin_yn = #{adminYN}
AND m.block_yn = #{blockYN}
AND m.user_del_yn = #{delYN}
ORDER BY m.join_date ASC
ORDER BY m.join_date DESC
ORDER BY m.access_date DESC
ORDER BY m.access_date ASC
ORDER BY m.join_date DESC
LIMIT #{size} OFFSET #{start}

SELECT COUNT(*) 
FROM member m


    AND m.id LIKE CONCAT('%', #{searchId}, '%')


    AND m.admin_yn = #{adminYN}


    AND m.block_yn = #{blockYN}


    AND m.user_del_yn = #{delYN}
