mapper.select:
SELECT count(*)
FROM member_block
WHERE blocker_id = #{id} AND blocked_id = #{blocked_id}

SELECT *
FROM member_block
WHERE blocker_id = #{id}
ORDER BY block_start_date DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM member_block
WHERE blocker_id = #{id}

mapper.update:
UPDATE member
SET block_yn = TRUE
WHERE id = #{blocked_id}

UPDATE member
SET block_yn = FALSE
WHERE id = #{blocked_id}
